import React, { useRef, useState } from "react";
import Button from 'react-bootstrap/Button';
import ConfirmDialog from "./confirmation";

let AppTodo = () => {
    const [todos, setTodos] = useState([]);
    let [isopneConfirm, setopneConfirm] = useState(false);
    let [deleteindex, setdeleteindex] = useState(null);

let inputselect = useRef()

let addtovaluetodo = () => {
    let value = inputselect.current.value

    let newItem = {
        completed: false,
        value,
    };

    setTodos([...todos, newItem]);
    inputselect.current.value = " ";
}




let tododone = (index) => {
    let todo = [...todos]
 todo[index].completed = !todo[index].completed 
 setTodos([...todo])

}

let deleteItem = (index) => {
     
    let todo = [...todos]
    todo.splice(index, 1)
    setTodos([...todo])

  
}


let opneConfirm = (index) => {
    setdeleteindex(index)
   setopneConfirm(true)
}

let closeConfirm = () => {
    
    setopneConfirm(false)
}



    return (
        <>
            <div className="container">
                <div className="todolist">
                <input ref={inputselect} className="input"/>
                <Button onClick={addtovaluetodo} className="btn" variant="outline-danger">Add</Button>
                <div className="list">
                    <ul>
                        {
                        todos.map(({value, completed}, index) => {
                            return (
                            <div key={index}  className="boxlist">
                                <li className={completed ? "done" : ""} onClick={() => tododone(index)}>{value}</li>
                                <span onClick={() => opneConfirm(index)}>delete</span>
                            </div>
                            )
                          })
                        }
                    </ul>
                </div>
                </div>

            </div>
            <ConfirmDialog isOpen={isopneConfirm} onCancel={closeConfirm}></ConfirmDialog>
        </>
    )
}

export default AppTodo
