import React, { useState } from 'react';
//import './css/style.css';
import Header from './Component/header';

import Toggle from './Component/togglebtn';

import ThemeContext from "./context"


const App = () => {
  let [color, setcolor] = useState('dark')

let toggletheme = () => {
 setcolor( color === "dark" ? "light" : "dark" )
}

  return (
    <ThemeContext.Provider value="">
    <div style={{background: color === "dark" ? "black" : "#fff"}}>
      <h1>hi from header</h1>
      <Header theme={color}></Header>
      <Toggle change={toggletheme}></Toggle>
    </div>
    </ThemeContext.Provider>
  )
}


export default App
