import React, { useEffect, useState } from 'react';
import './css/style.css';
import {Container} from 'react-bootstrap'
import Header from './componenets/header';
import ListAction from './componenets/listAction';
import CardFood from './componenets/card';
import {datafood} from './data'

const App = () => {
  let [itemsdata, setdata] = useState(datafood);
  let allgategory = ["الكل", ...new Set(datafood.map((i) => i.gategory))];


 let fillterGtegory = (cate) => {
     if(cate === "الكل") {
      setdata(datafood)
     }else {
      let itemsgategory =  datafood.filter((item) => {
        return item.gategory === cate
       })
       setdata(itemsgategory)
     }
 }

 let searsh = (world) => {
    if(world !== " ") {
      let searshfilterWorld = datafood.filter(function (item) {
          return item.includes() 
      })
    }
 }


      return (
        <div>
            <Header></Header>
            <ListAction action={fillterGtegory} allgategory={allgategory}></ListAction>
            <CardFood items={itemsdata}></CardFood>
        </div>
      )
}


export default App
