import React, { createContext, useState } from "react";

export const CounterCon = createContext(0)

let CounterContextProvider = (props) => {
    let [value, setValue] = useState(0);

    const increment = () => {
      setValue(value + 1);
    };
  
    const decrement = () => {
      setValue(value - 1);
    };

    return (
        <CounterCon.Provider value={{
            value,
            increment, 
            decrement
        }}>
            {props.children}
        </CounterCon.Provider>
    )
}

export default CounterContextProvider
