import React, { useEffect } from 'react';
import { Container, Form, Nav } from 'react-bootstrap';


let Header = () => {

    useEffect(() => {
        window.addEventListener("scroll", isscroll)
    })

    let isscroll = () => {
        let header = document.querySelector(".header")
        if(window.scrollY >= 200) {
            header.classList.add("active")
        }else {
            header.classList.remove("active")
        }
    }

    let openmenu = () => {
       let opennavber = document.querySelector(".navber-top")
      opennavber.style.width = "300px";
      opennavber.style.transition = "0.15s ease-in"
    }

    let closemenu = () => {
        let opennavber = document.querySelector(".navber-top")
       opennavber.style.width = "0";
       opennavber.style.transition = "0.15s ease-in"
     }
 

    return (
        <div className='header'>
            <Container className='container'>
             <div className='navber'>
                <div className='logo'>
                    <img src='/logo2.png'/>
                </div>

                <nav>

                <div className='logo'>
                    <img src='/logo2.png'/>
                </div>
                <div className='close'>
                <i class="fa-solid fa-square-xmark"></i>
                </div>
                    <ul>
                        <Nav.Link className='Link' href="/home">home</Nav.Link>
                        <Nav.Link className='Link' href="/home">MOVIE</Nav.Link>
                        <Nav.Link className='Link' href="/home">TV SHOW</Nav.Link>
                        <Nav.Link className='Link' href="/home">WEB SERIES</Nav.Link>
                        <Nav.Link className='Link' href="/home">PRICING</Nav.Link>
                    </ul>
                </nav>

                <div className='box'>
                    <div className='searsh'>
                        <input type='text' placeholder='search for movies'/>
                        <i class="fa-solid fa-magnifying-glass"></i>
                    </div>
                    <i class="fa-solid fa-caret-down"></i>

                        <select size='sm' className='select'>
                            <option className='text'>AU</option>
                            <option value="1">EN</option>
                            <option value="2">AR</option>
                        </select>

                        <div className='menu' onClick={ openmenu}>
                            <i class="fa-solid fa-bars"></i>
                        </div>

                        <button className='btnSIGN'>SIGN IN</button>

                </div>

                
                </div>
            </Container>
        </div>
    )
}

export default Header
