import React, { useEffect, useState }   from "react"
import { Container } from "react-bootstrap";
import {BrowserRouter, Routes, Route, useParams} from 'react-router-dom';

//https://api.themoviedb.org/3/movie/572802?api_key=2ed27428d49a7dff95d8705d52d37dee&page=1&!language=en-US
let MoviesDetails = ({setHero}) => {
    let idMovies = useParams()
    let [detailsMovies, setdetailsMovies] = useState()

    let getMoviesDetails = async() => {
        await fetch(`https://api.themoviedb.org/3/movie/${idMovies.id}?api_key=2ed27428d49a7dff95d8705d52d37dee&page=1&!language=en-US`)
         .then((response)=> {return response.json()})
         .then((listMovies) => {return setdetailsMovies(listMovies)})
     }

useEffect(() => {
    setHero(false)
    getMoviesDetails()
},[])

