import React, { useRef, useState } from "react";
import Button from 'react-bootstrap/Button';

const ConfirmDialog = ({ isOpen, onCancel, onConfirm }) => {
  return (
    <>
      {isOpen && (
        <div className="confirm-dialog">
          <p>هل أنت متأكد من حذف هذا العنصر؟</p>
          <Button onClick={onConfirm} variant="danger">نعم</Button>
          <Button onClick={onCancel} variant="secondary">لا</Button>
        </div>
      )}
    </>
  );
};

const AppTodo = () => {
  const [todos, setTodos] = useState([]);
  const [isConfirmOpen, setConfirmOpen] = useState(false);
  const [deleteIndex, setDeleteIndex] = useState(null);

  let inputselect = useRef();

  let addtovaluetodo = () => {
    let value = inputselect.current.value

    let newItem = {
      completed: false,
      value,
    };

    setTodos([...todos, newItem]);
    inputselect.current.value = " ";
  }

  let tododone = (index) => {
    let todo = [...todos];
    todo[index].completed = !todo[index].completed;
    setTodos([...todo]);
  }

  let openConfirm = (index) => {
    setDeleteIndex(index);
    setConfirmOpen(true);
  }

  let closeConfirm = () => {
    setDeleteIndex(null);
    setConfirmOpen(false);
  }

  let confirmDelete = () => {
    if (deleteIndex !== null) {
      let todo = [...todos];
      todo.splice(deleteIndex, 1);
      setTodos([...todo]);
      closeConfirm();
    }
  }

  return (
    <>
      <div className="container">
        <div className="todolist">
          <input ref={inputselect} className="input"/>
          <Button onClick={addtovaluetodo} className="btn" variant="outline-danger">Add</Button>
          <div className="list">
            <ul>
              {todos.map(({ value, completed }, index) => (
                <div key={index} className="boxlist">
                  <li className={completed ? "done" : ""} onClick={() => tododone(index)}>{value}</li>
                  <span onc={() => openConfirm(index)}>delete</span>
                </div>
              ))}
            </ul>
          </div>
        </div>
      </div>

    </>
  );
}

export default AppTodo;
