import React, { useRef, useState } from "react";
import Button from 'react-bootstrap/Button';

let AppTodo = () => {
    const [todos, setTodos] = useState([])

let inputselect = useRef()

let addtovaluetodo = () => {
    let value = inputselect.current.value
    let newItem = {
        completed: false,
        value,
    };
    setTodos([...todos, newItem])
    inputselect.current.value = " "
}


let tododone = (index) => {
    let todo = [...todos]
 todo[index].completed = !todo[index].completed 

}


    return (
        <>
            <div className="container">
                <div className="todolist">
                <input ref={inputselect} className="input"/>
                <Button onClick={addtovaluetodo} className="btn" variant="outline-danger">Add</Button>
                <div className="list">
                    <ul>
                        {
                        todos.map(({value}, index) => {
                            return (
                            <div key={index}  className="boxlist">
                                <li onClick={() => tododone(index)}>{value}</li>
                                <span>delete</span>
                            </div>
                            )
                          })
                        }
                    </ul>
                </div>
                </div>
            </div>
        </>
    )
}

export default AppTodo
