import React, { useEffect, useState } from "react";
import { Container } from "react-bootstrap";
import UpcomingAction from "./upcomingAction";
import MovieList from "./moviesList";

let SectionUpcoming = () => {
let [Movies, getMovies] = useState([])
    let getAllMovies = async() => {
       await fetch("https://api.themoviedb.org/3/movie/popular?api_key=2ed27428d49a7dff95d8705d52d37dee&!language=en-US&page=1")
        .then((response)=> {
            return response.json()
        }).then((listMovies) => {
          return getMovies(listMovies.results) 
  
        })
    }

    let search = () => {
        fetch("https://api.themoviedb.org/3/search/movie?api_key=2ed27428d49a7dff95d8705d52d37dee&query=Wonka&include_adult=false&language=en-US&page=1")
        .then(response => {
            return response.json()
        })
        .then(data => {
            return data
        })
    }

    useEffect(() => {
        getAllMovies()
        search()
    }, [])
console.log(Movies)
    return (
        <>
            <UpcomingAction></UpcomingAction>
            <MovieList movies={Movies}></MovieList>
        </>
    )
}

export default SectionUpcoming
